contracts/EscrowFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "./FreelancerFactory.sol";
import "./FreelancerProfile.sol";
import "./JobEscrow.sol";
import "./JobBoard.sol"; // ✅ NEW

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/// @title EscrowFactory
/// @notice Deploys per-job escrows, funds them atomically with USDT, and links them to the freelancer's profile.
/// @dev Flow:
/// 1) Client approves USDT to this factory
/// 2) Client calls createAndFundEscrow(...)
/// 3) (NEW OPTION) Client can call createAndFundEscrowForJob(jobId, ...) so JobBoard is marked as Hired
contract EscrowFactory is Ownable {
    using SafeERC20 for IERC20;

    /*//////////////////////////////////////////////////////////////
                                ERRORS
    //////////////////////////////////////////////////////////////*/
    error ZeroAddress();
    error ProfileMissing();
    error AmountZero();
    error BpsTooHigh();

    /*//////////////////////////////////////////////////////////////
                                EVENTS
    //////////////////////////////////////////////////////////////*/
    event EscrowCreated(
        bytes32 indexed jobKey,
        address indexed escrow,
        address indexed client,
        address freelancer,
        uint256 amount,
        uint64 cancelWindowEnd,
        uint64 deliveryDue,
        uint64 reviewDue
    );

    /*//////////////////////////////////////////////////////////////
                                STATE
    //////////////////////////////////////////////////////////////*/
    IERC20 public immutable usdt;
    FreelancerFactory public immutable freelancerFactory;
    JobBoard public immutable jobBoard; // ✅ NEW

    // Fee on payouts (not on refunds), in basis points (max 10000)
    uint16  public platformFeeBps;         // e.g., 200 = 2%
    address public platformWallet;

    // Dispute resolver (EOA/multisig/module)
    address public resolver;

    constructor(
        address _freelancerFactory,
        address _usdt,
        address _platformWallet,
        uint16  _platformFeeBps,
        address _resolver,
        address _jobBoard           // ✅ NEW PARAM
    ) {
        _transferOwnership(msg.sender);

        if (
            _freelancerFactory == address(0) ||
            _usdt == address(0) ||
            _platformWallet == address(0) ||
            _resolver == address(0) ||
            _jobBoard == address(0)        // ✅ NEW CHECK
        ) revert ZeroAddress();
        if (_platformFeeBps > 10_000) revert BpsTooHigh();

        freelancerFactory = FreelancerFactory(_freelancerFactory);
        usdt = IERC20(_usdt);
        platformWallet = _platformWallet;
        platformFeeBps = _platformFeeBps;
        resolver = _resolver;
        jobBoard = JobBoard(_jobBoard);    // ✅ STORE JOBBOARD
    }


    /*//////////////////////////////////////////////////////////////
                                ADMIN
    //////////////////////////////////////////////////////////////*/
    function setPlatformFee(uint16 _bps, address _wallet) external onlyOwner {
        if (_wallet == address(0)) revert ZeroAddress();
        if (_bps > 10_000) revert BpsTooHigh();
        platformFeeBps = _bps;
        platformWallet = _wallet;
    }

    function setResolver(address _resolver) external onlyOwner {
        if (_resolver == address(0)) revert ZeroAddress();
        resolver = _resolver;
    }

    /*//////////////////////////////////////////////////////////////
                                CREATE + FUND
    //////////////////////////////////////////////////////////////*/
    /// @notice Deploy & fund a per-job escrow atomically.
    /// @param freelancer Freelancer wallet (profile owner).
    /// @param amountUSDT Amount to lock inside the escrow (client must approve this factory first).
    /// @param metadataURI Off-chain job metadata (optional; stored/emitted by escrow as needed).
    /// @param cancelWindowSecs Pre-delivery unilateral cancel window for client.
    /// @param deliveryDueTs Hard delivery deadline (0 to disable).
    /// @param reviewWindowSecs Client review window after delivery (0 disables auto-approve).
    /// @param autoApproveDefaultRating Rating used on auto-approve (clamped to 1..5 if >0).
    function createAndFundEscrow(
        address freelancer,
        uint256 amountUSDT,
        string calldata metadataURI,
        uint64  cancelWindowSecs,
        uint64  deliveryDueTs,
        uint64  reviewWindowSecs,
        uint8   autoApproveDefaultRating
    ) public returns (address escrowAddr) { // ✅ was external
        if (freelancer == address(0)) revert ZeroAddress();
        if (amountUSDT == 0) revert AmountZero();

        // Resolve freelancer profile
        address profileAddr = freelancerFactory.freelancerProfile(freelancer);
        if (profileAddr == address(0)) revert ProfileMissing();

        // Deploy JobEscrow
        JobEscrow escrow = new JobEscrow(
            msg.sender,                 // client (EOA / smart account)
            freelancer,                 // freelancer
            address(usdt),              // USDT token
            amountUSDT,
            metadataURI,
            platformFeeBps,
            platformWallet,
            resolver,
            profileAddr,
            cancelWindowSecs,
            deliveryDueTs,
            reviewWindowSecs,
            autoApproveDefaultRating,
            address(this)               // escrowFactory
        );
        escrowAddr = address(escrow);

        // Pull USDT from client → escrow
        usdt.safeTransferFrom(msg.sender, escrowAddr, amountUSDT);

        // Link escrow into freelancer profile (factory-level authorization)
        freelancerFactory.linkEscrowToProfile(freelancer, escrowAddr);

        // Tell escrow to register the job in the profile and move to InProgress
        escrow.bootstrapRegisterJob();

        // Read deadlines for event
        (uint64 cancelEnd, uint64 deliveryDue, uint64 reviewDue) = escrow.currentDeadlines();

        emit EscrowCreated(
            escrow.jobKey(),
            escrowAddr,
            msg.sender,
            freelancer,
            amountUSDT,
            cancelEnd,
            deliveryDue,
            reviewDue
        );

        return escrowAddr;
    }

    /// @notice NEW helper: same as createAndFundEscrow, but also marks the JobBoard job as Hired.
    /// @dev This does NOT change the old function; it's an extra entrypoint for flows that know jobId.
    function createAndFundEscrowForJob(
        uint256 jobId,
        address freelancer,
        uint256 amountUSDT,
        string calldata metadataURI,
        uint64  cancelWindowSecs,
        uint64  deliveryDueTs,
        uint64  reviewWindowSecs,
        uint8   autoApproveDefaultRating
    ) external returns (address escrowAddr) {
        // ✅ INTERNAL CALL – preserves original msg.sender (the client smart wallet)
        escrowAddr = createAndFundEscrow(
            freelancer,
            amountUSDT,
            metadataURI,
            cancelWindowSecs,
            deliveryDueTs,
            reviewWindowSecs,
            autoApproveDefaultRating
        );

        // ✅ After escrow deployed & funded, mark the job as hired on JobBoard
        jobBoard.markAsHired(jobId, freelancer, escrowAddr);

        return escrowAddr;
    }
}

contracts/FreelancerFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "./FreelancerProfile.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

/// @title FreelancerFactory
/// @notice Deploys FreelancerProfile instances, manages KYC verification & escrow registration.
contract FreelancerFactory {
    using ECDSA for bytes32;

    /*//////////////////////////////////////////////////////////////
                                ERRORS
    //////////////////////////////////////////////////////////////*/
    error NotOwner();
    error ProfileExists();
    error ZeroAddress();
    error NoProfile();
    error AttesterNotAuthorized();
    error SignatureExpired();
    error InvalidNonce();
    error NotEscrowDeployerOrOwner();

    /*//////////////////////////////////////////////////////////////
                                EVENTS
    //////////////////////////////////////////////////////////////*/
    event ProfileDeployed(address indexed freelancer, address indexed profile);
    event KYCSetByOwner(address indexed freelancer, address indexed profile, bool status);
    event KYCSetByAttestation(address indexed freelancer, address indexed profile, bool status, address indexed attester);
    event AttesterSet(address indexed attester, bool enabled);
    event OwnerChanged(address indexed previousOwner, address indexed newOwner);
    event EscrowDeployerSet(address indexed deployer, bool enabled);
    event EscrowLinked(address indexed freelancer, address indexed profile, address indexed escrow);

    /*//////////////////////////////////////////////////////////////
                                STATE
    //////////////////////////////////////////////////////////////*/
    address public owner;

    // Maps freelancer wallet → deployed profile contract
    mapping(address => address) public freelancerProfile;

    // ✅ NEW: Array for UI discovery — keeps all freelancers that created profiles
    address[] private allFreelancers;

    // Attester management for signature-based KYC
    mapping(address => bool) public attesters;

    // Nonce per freelancer to prevent replay of signed attestations
    mapping(address => uint256) public nonces;

    // Addresses that can link escrows (e.g. EscrowFactory)
    mapping(address => bool) public escrowDeployers;

    constructor() {
        owner = msg.sender;
    }

    /*//////////////////////////////////////////////////////////////
                                MODIFIERS
    //////////////////////////////////////////////////////////////*/
    modifier onlyOwner() {
        if (msg.sender != owner) revert NotOwner();
        _;
    }

    modifier onlyEscrowDeployerOrOwner() {
        if (msg.sender != owner && !escrowDeployers[msg.sender]) revert NotEscrowDeployerOrOwner();
        _;
    }

    /*//////////////////////////////////////////////////////////////
                                OWNER / ADMIN
    //////////////////////////////////////////////////////////////*/
    function transferOwnership(address _newOwner) external onlyOwner {
        if (_newOwner == address(0)) revert ZeroAddress();
        address previous = owner;
        owner = _newOwner;
        emit OwnerChanged(previous, _newOwner);
    }

    function setAttester(address _attester, bool _enabled) external onlyOwner {
        if (_attester == address(0)) revert ZeroAddress();
        attesters[_attester] = _enabled;
        emit AttesterSet(_attester, _enabled);
    }

    function setEscrowDeployer(address _deployer, bool _enabled) external onlyOwner {
        if (_deployer == address(0)) revert ZeroAddress();
        escrowDeployers[_deployer] = _enabled;
        emit EscrowDeployerSet(_deployer, _enabled);
    }

    /*//////////////////////////////////////////////////////////////
                                PROFILE DEPLOYMENT
    //////////////////////////////////////////////////////////////*/
    /// @notice Deploys a FreelancerProfile for the sender (one per wallet)
    function deployFreelancerProfile(
        string calldata _name,
        string calldata _bio,
        string calldata _profileURI
    ) external returns (address profileAddr) {
        address freelancer = msg.sender;
        if (freelancerProfile[freelancer] != address(0)) revert ProfileExists();

        FreelancerProfile profile = new FreelancerProfile(
            freelancer,
            address(this),
            _name,
            _bio,
            _profileURI
        );

        freelancerProfile[freelancer] = address(profile);

        // ✅ Track freelancer for frontend discovery
        allFreelancers.push(freelancer);

        emit ProfileDeployed(freelancer, address(profile));
        return address(profile);
    }

    /*//////////////////////////////////////////////////////////////
                                PUBLIC VIEWS (for UI)
    //////////////////////////////////////////////////////////////*/
    /// @notice Returns all freelancer wallet addresses that have created a profile
    function getAllFreelancers() external view returns (address[] memory) {
        return allFreelancers;
    }

    /// @notice Returns both freelancer wallets and profile contract addresses
    function getAllFreelancerProfiles()
        external
        view
        returns (address[] memory wallets, address[] memory profiles)
    {
        uint256 count = allFreelancers.length;
        wallets = new address[](count);
        profiles = new address[](count);
        for (uint256 i = 0; i < count; i++) {
            address wallet = allFreelancers[i];
            wallets[i] = wallet;
            profiles[i] = freelancerProfile[wallet];
        }
    }

    /// @notice Returns total number of freelancers (for pagination)
    function totalFreelancers() external view returns (uint256) {
        return allFreelancers.length;
    }

    /*//////////////////////////////////////////////////////////////
                                ESCROW LINKING
    //////////////////////////////////////////////////////////////*/
    /// @notice Link a new escrow contract to a freelancer’s profile.
    function linkEscrowToProfile(address freelancer, address escrowAddr)
        external
        onlyEscrowDeployerOrOwner
    {
        if (freelancer == address(0) || escrowAddr == address(0)) revert ZeroAddress();
        address profileAddr = freelancerProfile[freelancer];
        if (profileAddr == address(0)) revert NoProfile();

        FreelancerProfile(profileAddr).registerEscrow(escrowAddr);
        emit EscrowLinked(freelancer, profileAddr, escrowAddr);
    }

    /*//////////////////////////////////////////////////////////////
                                KYC MANAGEMENT
    //////////////////////////////////////////////////////////////*/
    /// @notice Set KYC manually (only owner)
    function setKYCFor(address freelancer, bool status) external onlyOwner {
        address profileAddr = freelancerProfile[freelancer];
        if (profileAddr == address(0)) revert NoProfile();

        FreelancerProfile(profileAddr).setKYCVerified(status);
        emit KYCSetByOwner(freelancer, profileAddr, status);
    }

    /// @notice Set KYC via signed attestation
    function setKYCByAttestation(
        address freelancer,
        bool status,
        uint256 nonce,
        uint256 expiry,
        bytes calldata signature
    ) external {
        if (block.timestamp > expiry) revert SignatureExpired();
        if (nonce != nonces[freelancer]) revert InvalidNonce();

        bytes32 dataHash = keccak256(
            abi.encode(freelancer, status, nonce, expiry, address(this))
        );
        bytes32 ethSigned = ECDSA.toEthSignedMessageHash(dataHash);
        address signer = ECDSA.recover(ethSigned, signature);
        if (!attesters[signer]) revert AttesterNotAuthorized();

        nonces[freelancer] = nonce + 1;

        address profileAddr = freelancerProfile[freelancer];
        if (profileAddr == address(0)) revert NoProfile();

        FreelancerProfile(profileAddr).setKYCVerified(status);
        emit KYCSetByAttestation(freelancer, profileAddr, status, signer);
    }
}

contracts/FreelancerProfile.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/// @title FreelancerProfile
/// @notice Per-freelancer profile. KYC is controlled ONLY by the Factory.
///         Adds secure escrow registration and a multi-job state machine with points/levels.
contract FreelancerProfile {
    /*//////////////////////////////////////////////////////////////
                                ERRORS
    //////////////////////////////////////////////////////////////*/
    error NotOwner();
    error NotFactory();
    error NotVerified();
    error MaxServicesReached();
    error ServiceNotFound();
    error EscrowNotAuthorized();
    error EscrowAlreadyRegistered();
    error ZeroAddress();

    error JobExists();
    error JobUnknown();
    error JobWrongEscrow();
    error JobAlreadyFinalized();
    error JobNotApproved();
    error InvalidStatusTransition();

    /*//////////////////////////////////////////////////////////////
                                EVENTS
    //////////////////////////////////////////////////////////////*/
    event ProfileUpdated(string name, string bio, string profileURI);
    event KYCStatusChanged(address indexed setter, bool indexed status);
    event ServiceAdded(uint256 indexed id, string title);

    event EscrowRegistered(address indexed escrow);
    event EscrowUnregistered(address indexed escrow);

    // Job lifecycle & reputation
    event JobRegistered(bytes32 indexed jobKey, address indexed escrow);
    event JobStatusChanged(bytes32 indexed jobKey, address indexed escrow, JobStatus oldStatus, JobStatus newStatus);
    event JobCompleted(bytes32 indexed jobKey, address indexed escrow, uint8 rating, uint256 totalPoints, uint256 completedJobs, uint8 newLevel);
    event PointsUpdated(uint256 oldPoints, uint256 newPoints);
    event LevelUpdated(uint8 oldLevel, uint8 newLevel);

    /*//////////////////////////////////////////////////////////////
                                STATE
    //////////////////////////////////////////////////////////////*/
    address public immutable owner;    // freelancer wallet (owner)
    address public immutable factory;  // factory that deployed this profile

    string public name;
    string public bio;
    string public profileURI;

    bool public isKYCVerified;

    // Reputation totals (points = sum of per-job star ratings; jobs = completed count)
    uint256 public totalPoints;
    uint256 public completedJobs;
    uint8   public level; // starts at 0

    // Escrow registry
    mapping(address => bool) public activeEscrows; // escrow contract => registered (allowed to manage jobs)

    // Job state & replay protection
    enum JobStatus { None, Created, InProgress, Delivered, Approved, Disputed, Cancelled, Completed }

    struct Job {
        address escrow;
        JobStatus status;
        uint8  rating;       // 1..5 (capped)
        bool   finalized;    // true after completion accounted in totals
        uint64 createdAt;
        uint64 updatedAt;
    }

    mapping(bytes32 => Job) private jobs;          // jobKey => Job
    mapping(bytes32 => bool) public jobProcessed;  // kept for simple external checks (mirrors .finalized)

    // Services (unchanged)
    struct Service {
        string title;
        string description;
        string imageURI;
        string videoURI;
        uint256 priceUSDC;
        bool active;
    }
    mapping(uint256 => Service) public services;
    uint256 public serviceCount;

    /*//////////////////////////////////////////////////////////////
                                MODIFIERS
    //////////////////////////////////////////////////////////////*/
    modifier onlyOwner() {
        if (msg.sender != owner) revert NotOwner();
        _;
    }

    modifier onlyFactory() {
        if (msg.sender != factory) revert NotFactory();
        _;
    }

    modifier onlyVerified() {
        if (!isKYCVerified) revert NotVerified();
        _;
    }

    modifier onlyEscrow() {
        if (!activeEscrows[msg.sender]) revert EscrowNotAuthorized();
        _;
    }

    /*//////////////////////////////////////////////////////////////
                                CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/
    constructor(
        address _owner,
        address _factory,
        string memory _name,
        string memory _bio,
        string memory _profileURI
    ) {
        if (_owner == address(0) || _factory == address(0)) revert ZeroAddress();
        owner = _owner;
        factory = _factory;

        name = _name;
        bio = _bio;
        profileURI = _profileURI;

        // initial reputation
        totalPoints = 0;
        completedJobs = 0;
        level = 0; // ✅ as requested: start at level 0

        emit ProfileUpdated(_name, _bio, _profileURI);
    }

    /*//////////////////////////////////////////////////////////////
                                PROFILE
    //////////////////////////////////////////////////////////////*/
    function updateProfile(
        string calldata _name,
        string calldata _bio,
        string calldata _profileURI
    ) external onlyOwner {
        name = _name;
        bio = _bio;
        profileURI = _profileURI;
        emit ProfileUpdated(_name, _bio, _profileURI);
    }

    /*//////////////////////////////////////////////////////////////
                                KYC (only factory)
    //////////////////////////////////////////////////////////////*/
    function setKYCVerified(bool _status) external onlyFactory {
        if (isKYCVerified == _status) {
            emit KYCStatusChanged(msg.sender, _status);
            return;
        }
        isKYCVerified = _status;
        emit KYCStatusChanged(msg.sender, _status);
    }

    /*//////////////////////////////////////////////////////////////
                                ESCROWS (Factory only)
    //////////////////////////////////////////////////////////////*/
    function registerEscrow(address escrowAddr) external onlyFactory {
        if (escrowAddr == address(0)) revert ZeroAddress();
        if (activeEscrows[escrowAddr]) revert EscrowAlreadyRegistered();
        activeEscrows[escrowAddr] = true;
        emit EscrowRegistered(escrowAddr);
    }

    function unregisterEscrow(address escrowAddr) external onlyFactory {
        if (escrowAddr == address(0)) revert ZeroAddress();
        if (!activeEscrows[escrowAddr]) revert EscrowNotAuthorized();
        activeEscrows[escrowAddr] = false;
        emit EscrowUnregistered(escrowAddr);
    }

    /*//////////////////////////////////////////////////////////////
                                SERVICES (KYC gated)
    //////////////////////////////////////////////////////////////*/
    function addService(
        string calldata _title,
        string calldata _description,
        string calldata _imageURI,
        string calldata _videoURI,
        uint256 _priceUSDC
    ) external onlyOwner onlyVerified {
        if (serviceCount >= 100) revert MaxServicesReached();
        services[serviceCount] = Service({
            title: _title,
            description: _description,
            imageURI: _imageURI,
            videoURI: _videoURI,
            priceUSDC: _priceUSDC,
            active: true
        });
        emit ServiceAdded(serviceCount, _title);
        unchecked { serviceCount += 1; }
    }

    /*//////////////////////////////////////////////////////////////
                        JOB LIFECYCLE (multi-job support)
    //////////////////////////////////////////////////////////////*/
    /// @notice Register a new job for this profile. Called by a registered escrow.
    /// @param jobKey A unique identifier for the job (e.g., keccak256(abi.encodePacked(escrow, jobId))).
    function registerJob(bytes32 jobKey) external onlyEscrow {
        if (jobKey == bytes32(0)) revert JobUnknown();
        Job storage j = jobs[jobKey];
        if (j.status != JobStatus.None) revert JobExists();

        jobs[jobKey] = Job({
            escrow: msg.sender,
            status: JobStatus.Created,
            rating: 0,
            finalized: false,
            createdAt: uint64(block.timestamp),
            updatedAt: uint64(block.timestamp)
        });

        emit JobRegistered(jobKey, msg.sender);
        emit JobStatusChanged(jobKey, msg.sender, JobStatus.None, JobStatus.Created);
    }

    /// @notice Update job status along allowed transitions (Created→InProgress→Delivered→Approved/Disputed→Completed or Cancelled).
    function updateJobStatus(bytes32 jobKey, JobStatus newStatus) external onlyEscrow {
        Job storage j = _mustGetJob(jobKey);
        if (j.escrow != msg.sender) revert JobWrongEscrow();
        if (j.finalized) revert JobAlreadyFinalized();

        JobStatus old = j.status;
        if (!_allowedTransition(old, newStatus)) revert InvalidStatusTransition();

        j.status = newStatus;
        j.updatedAt = uint64(block.timestamp);
        emit JobStatusChanged(jobKey, msg.sender, old, newStatus);
    }

    /// @notice Finalize an APPROVED job: caps rating to 1..5, prevents double-counting, updates totals & level.
    /// @dev Only the escrow that owns the job can finalize it. Requires status == Approved.
    function markJobCompleted(uint8 ratingStars, bytes32 jobKey) external onlyEscrow {
        Job storage j = _mustGetJob(jobKey);
        if (j.escrow != msg.sender) revert JobWrongEscrow();
        if (j.finalized) revert JobAlreadyFinalized();
        if (j.status != JobStatus.Approved) revert JobNotApproved();

        // cap rating to 1..5 (if you want to allow 0, change the min below)
        if (ratingStars < 1) ratingStars = 1;
        if (ratingStars > 5) ratingStars = 5;

        // finalize
        j.rating = ratingStars;
        j.status = JobStatus.Completed;
        j.finalized = true;
        j.updatedAt = uint64(block.timestamp);
        jobProcessed[jobKey] = true; // mirror finalized for external quick checks

        // update totals
        uint256 oldPoints = totalPoints;
        totalPoints += ratingStars;  // each job contributes up to 5 points
        completedJobs += 1;
        emit PointsUpdated(oldPoints, totalPoints);

        // compute new level using dual thresholds (jobs & points)
        uint8 oldLevel = level;
        level = _computeLevel(completedJobs, totalPoints);
        if (level != oldLevel) emit LevelUpdated(oldLevel, level);

        emit JobCompleted(jobKey, msg.sender, ratingStars, totalPoints, completedJobs, level);
    }

    /*//////////////////////////////////////////////////////////////
                        INTERNAL: HELPERS & LEVEL LOGIC
    //////////////////////////////////////////////////////////////*/
    function _mustGetJob(bytes32 jobKey) internal view returns (Job storage j) {
        j = jobs[jobKey];
        if (j.status == JobStatus.None) revert JobUnknown();
    }

    function _allowedTransition(JobStatus from, JobStatus to) internal pure returns (bool) {
        if (from == JobStatus.Created && (to == JobStatus.InProgress || to == JobStatus.Cancelled)) return true;
        if (from == JobStatus.InProgress && (to == JobStatus.Delivered || to == JobStatus.Cancelled || to == JobStatus.Disputed)) return true;
        if (from == JobStatus.Delivered && (to == JobStatus.Approved || to == JobStatus.Disputed || to == JobStatus.Cancelled)) return true;
        if (from == JobStatus.Approved && (to == JobStatus.Completed)) return true; // completion happens via markJobCompleted
        if (from == JobStatus.Disputed && (to == JobStatus.Approved || to == JobStatus.Cancelled)) return true;
        // Completed/Cancelled are terminal in this model
        return false;
    }

    /// @dev Dual-threshold (jobs & points) level computation — starts at 0 and maxes at 5.
    function _computeLevel(uint256 _jobs, uint256 points) internal pure returns (uint8) {
        // totals required for each level (inclusive)
        // L1: 5 jobs & 20 pts
        // L2: 10 jobs & 45 pts
        // L3: 15 jobs & 70 pts
        // L4: 20 jobs & 95 pts
        // L5: 25 jobs & 120 pts
        if (_jobs >= 25 && points >= 120) return 5;
        if (_jobs >= 20 && points >= 95)  return 4;
        if (_jobs >= 15 && points >= 70)  return 3;
        if (_jobs >= 10 && points >= 45)  return 2;
        if (_jobs >= 5  && points >= 20)  return 1;
        return 0;
    }

    /*//////////////////////////////////////////////////////////////
                                VIEWS
    //////////////////////////////////////////////////////////////*/
    /// @notice Helper to check whether an escrow is active for this profile
    function isEscrowActive(address escrowAddr) external view returns (bool) {
        return activeEscrows[escrowAddr];
    }

    /// @notice Helper to check if a jobKey has already been processed (finalized) for this profile
    /// @dev This is a simple read-only convenience for UI/indexers to know if a job was finalized (counted).
    function isJobProcessed(bytes32 jobKey) external view returns (bool) {
        return jobProcessed[jobKey];
    }

    /// @notice Returns job info for dashboards (escrow, status, rating, finalized, timestamps)
    function jobInfo(bytes32 jobKey)
        external
        view
        returns (
            address escrow,
            JobStatus status,
            uint8 rating,
            bool finalized,
            uint64 createdAt,
            uint64 updatedAt
        )
    {
        Job storage j = jobs[jobKey];
        return (j.escrow, j.status, j.rating, j.finalized, j.createdAt, j.updatedAt);
    }
}

contracts/JobBoard.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/**
 * @title JobBoard v3 — On-chain Job Board with Applications + Proposals
 */

interface IUSDCMinimal {
    function transfer(address to, uint256 value) external returns (bool);
    function transferFrom(address from, address to, uint256 value) external returns (bool);
}

interface IFreelancerFactory {
    function freelancerProfile(address freelancer) external view returns (address);
}

contract JobBoard {

    /*//////////////////////////////////////////////////////////////
                                ERRORS
    //////////////////////////////////////////////////////////////*/
    error NotOwner();
    error NotClient();
    error NotAllowedFactory();
    error ZeroAddress();
    error NotOpen();
    error InvalidStatus();
    error JobNotFound();
    error AlreadyHiredOrClosed();
    error TooManyTags();
    error NotReopenable();
    error NotExpired();
    error KYCGated();
    error WrongBond();
    error USDCNotSet();
    error Reentrancy();

    // NEW APPLICATION ERRORS
    error AlreadyApplied();
    error ApplicationNotFound();
    error NotFreelancer();

    /*//////////////////////////////////////////////////////////////
                                EVENTS
    //////////////////////////////////////////////////////////////*/
    event OwnerChanged(address indexed previousOwner, address indexed newOwner);
    event FactoryAllowed(address indexed factory, bool allowed);
    event PausedSet(bool paused);
    event GasSponsorshipSet(bool enabled);
    event AntiSpamModeSet(uint8 mode, uint256 postingBondUSDC);
    event USDCSet(address indexed usdc);
    event KYCSet(address indexed user, bool isVerified);

    event JobPosted(
        uint256 indexed jobId,
        address indexed client,
        string title,
        string descriptionURI,
        uint256 budgetUSDC,
        bytes32[] tags,
        uint64 expiresAt
    );
    event JobUpdated(
        uint256 indexed jobId,
        string title,
        string descriptionURI,
        uint256 budgetUSDC,
        bytes32[] tags,
        uint64 expiresAt
    );
    event JobCancelled(uint256 indexed jobId, address indexed client);
    event JobHired(
        uint256 indexed jobId,
        address indexed client,
        address indexed freelancer,
        address escrow
    );
    event JobCompleted(
        uint256 indexed jobId,
        address indexed client,
        address indexed freelancer,
        address escrow
    );
    event JobExpiredClosed(uint256 indexed jobId, uint64 expiresAt);
    event JobReopened(uint256 indexed jobId, uint64 newExpiresAt);

    // NEW EVENTS
    event FreelancerFactorySet(address indexed factory);
    event RequireApplicationToHireSet(bool required);
    event JobApplied(uint256 indexed jobId, address indexed freelancer, uint64 appliedAt);
    event JobApplicationWithdrawn(uint256 indexed jobId, address indexed freelancer);

    /*//////////////////////////////////////////////////////////////
                                STATE
    //////////////////////////////////////////////////////////////*/
    address public owner;
    bool public paused;
    mapping(address => bool) public allowedFactory;

    enum AntiSpamMode { None, BondRequired, OnlyKYC }
    AntiSpamMode public antiSpamMode;

    IUSDCMinimal public usdc;
    uint256 public postingBondUSDC;
    mapping(address => bool) public isKYC;
    bool public gasSponsorshipEnabled;

    enum JobStatus { Unknown, Open, Hired, Cancelled, Completed, Expired }

    struct Job {
        address client;
        string title;
        string descriptionURI;
        uint256 budgetUSDC;
        JobStatus status;
        address hiredFreelancer;
        address escrow;
        uint64 createdAt;
        uint64 updatedAt;
        uint64 expiresAt;
        bytes32[] tags;
        uint256 postingBond;
    }

    uint256 public nextJobId;
    mapping(uint256 => Job) public jobs;
    mapping(address => uint256[]) public jobsOf;

    uint256[] private openJobIds;
    mapping(uint256 => uint256) private openIndex;
    uint256 private locked = 1;

    /*//////////////////////////////////////////////////////////////
                        APPLICATION SYSTEM STORAGE
    //////////////////////////////////////////////////////////////*/
    IFreelancerFactory public freelancerFactory;
    bool public requireApplicationToHire;

    struct Applicant {
        address freelancer;
        uint64 appliedAt;
        string proposalURI;
        uint256 bidAmount;
        uint64 deliveryDays;
    }

    // jobId => list of applicants
    mapping(uint256 => Applicant[]) private applicantsOf;
    // jobId => freelancer => index+1
    mapping(uint256 => mapping(address => uint256)) private applicantIndex;
    // freelancer => applied jobIds
    mapping(address => uint256[]) private jobsAppliedBy;

    /*//////////////////////////////////////////////////////////////
                                MODIFIERS
    //////////////////////////////////////////////////////////////*/
    modifier onlyOwner() {
        if (msg.sender != owner) revert NotOwner();
        _;
    }

    modifier onlyOwnerOrFactory() {
        if (msg.sender != owner && !allowedFactory[msg.sender]) revert NotAllowedFactory();
        _;
    }

    modifier onlyAllowedFactory() {
        if (!allowedFactory[msg.sender]) revert NotAllowedFactory();
        _;
    }

    modifier onlyClient(uint256 jobId) {
        Job storage j = jobs[jobId];
        if (j.client == address(0)) revert JobNotFound();
        if (msg.sender != j.client) revert NotClient();
        _;
    }

    modifier whenNotPaused() {
        if (paused) revert InvalidStatus();
        _;
    }

    modifier nonReentrant() {
        if (locked != 1) revert Reentrancy();
        locked = 2;
        _;
        locked = 1;
    }

    constructor(address _owner) {
        if (_owner == address(0)) revert ZeroAddress();
        owner = _owner;
        nextJobId = 1;
        emit OwnerChanged(address(0), _owner);
    }

    /*//////////////////////////////////////////////////////////////
                                ADMIN
    //////////////////////////////////////////////////////////////*/
    function transferOwnership(address _newOwner) external onlyOwner {
        if (_newOwner == address(0)) revert ZeroAddress();
        address prev = owner;
        owner = _newOwner;
        emit OwnerChanged(prev, _newOwner);
    }

    function setAllowedFactory(address factory, bool allowed) external onlyOwner {
        if (factory == address(0)) revert ZeroAddress();
        allowedFactory[factory] = allowed;
        emit FactoryAllowed(factory, allowed);
    }

    function setPaused(bool _paused) external onlyOwner {
        paused = _paused;
        emit PausedSet(_paused);
    }

    function setGasSponsorship(bool enabled) external onlyOwner {
        gasSponsorshipEnabled = enabled;
        emit GasSponsorshipSet(enabled);
    }

    function setUSDC(address token) external onlyOwner {
        if (token == address(0)) revert ZeroAddress();
        usdc = IUSDCMinimal(token);
        emit USDCSet(token);
    }

    function setAntiSpamMode(uint8 mode, uint256 bondAmount) external onlyOwner {
        if (mode > uint8(AntiSpamMode.OnlyKYC)) revert InvalidStatus();
        antiSpamMode = AntiSpamMode(mode);
        postingBondUSDC = bondAmount;
        emit AntiSpamModeSet(mode, bondAmount);
    }

    function setKYC(address user, bool verified) external onlyOwnerOrFactory {
        if (user == address(0)) revert ZeroAddress();
        isKYC[user] = verified;
        emit KYCSet(user, verified);
    }

    function setFreelancerFactory(address factory_) external onlyOwner {
        freelancerFactory = IFreelancerFactory(factory_);
        emit FreelancerFactorySet(factory_);
    }

    function setRequireApplicationToHire(bool required) external onlyOwner {
        requireApplicationToHire = required;
        emit RequireApplicationToHireSet(required);
    }

    /*//////////////////////////////////////////////////////////////
                            CLIENT ACTIONS
    //////////////////////////////////////////////////////////////*/
    function postJob(
        string calldata title,
        string calldata descriptionURI,
        uint256 budgetUSDC,
        bytes32[] calldata tags,
        uint64 expiresAt
    ) external whenNotPaused nonReentrant returns (uint256 jobId) {

        if (antiSpamMode == AntiSpamMode.OnlyKYC && !isKYC[msg.sender]) revert KYCGated();

        uint256 bond = 0;
        if (antiSpamMode == AntiSpamMode.BondRequired) {
            if (address(usdc) == address(0)) revert USDCNotSet();
            if (postingBondUSDC == 0) revert WrongBond();
            bond = postingBondUSDC;
            bool ok = usdc.transferFrom(msg.sender, address(this), bond);
            if (!ok) revert WrongBond();
        }

        if (tags.length > 5) revert TooManyTags();
        if (expiresAt != 0 && expiresAt <= block.timestamp) revert InvalidStatus();

        jobId = nextJobId++;
        Job storage j = jobs[jobId];
        j.client = msg.sender;
        j.title = title;
        j.descriptionURI = descriptionURI;
        j.budgetUSDC = budgetUSDC;
        j.status = JobStatus.Open;
        j.createdAt = uint64(block.timestamp);
        j.updatedAt = uint64(block.timestamp);
        j.expiresAt = expiresAt;
        j.postingBond = bond;

        if (tags.length > 0) {
            j.tags = new bytes32[](tags.length);
            for (uint256 i = 0; i < tags.length; i++) {
                j.tags[i] = tags[i];
            }
        }

        jobsOf[msg.sender].push(jobId);
        _openAdd(jobId);

        emit JobPosted(jobId, msg.sender, title, descriptionURI, budgetUSDC, j.tags, expiresAt);
    }

    function updateJob(
        uint256 jobId,
        string calldata title,
        string calldata descriptionURI,
        uint256 budgetUSDC,
        bytes32[] calldata tags,
        uint64 expiresAt
    ) external whenNotPaused onlyClient(jobId) {
        Job storage j = jobs[jobId];
        if (j.status != JobStatus.Open) revert NotOpen();
        if (tags.length > 5) revert TooManyTags();
        if (expiresAt != 0 && expiresAt <= block.timestamp) revert InvalidStatus();

        j.title = title;
        j.descriptionURI = descriptionURI;
        j.budgetUSDC = budgetUSDC;
        j.updatedAt = uint64(block.timestamp);
        j.expiresAt = expiresAt;

        delete j.tags;
        if (tags.length > 0) {
            j.tags = new bytes32[](tags.length);
            for (uint256 i = 0; i < tags.length; i++) {
                j.tags[i] = tags[i];
            }
        }

        emit JobUpdated(jobId, title, descriptionURI, budgetUSDC, j.tags, expiresAt);
    }

    function cancelJob(uint256 jobId) 
        external 
        whenNotPaused 
        onlyClient(jobId) 
        nonReentrant 
    {
        Job storage j = jobs[jobId];
        if (j.status != JobStatus.Open) revert NotOpen();

        j.status = JobStatus.Cancelled;
        j.updatedAt = uint64(block.timestamp);

        _openRemove(jobId);
        _refundBondIfAny(j);

        emit JobCancelled(jobId, j.client);
    }

    function reopen(uint256 jobId, uint64 newExpiresAt) 
        external 
        whenNotPaused 
        onlyClient(jobId) 
    {
        Job storage j = jobs[jobId];

        if (j.status != JobStatus.Cancelled && j.status != JobStatus.Expired) revert NotReopenable();
        if (newExpiresAt != 0 && newExpiresAt <= block.timestamp) revert InvalidStatus();

        j.status = JobStatus.Open;
        j.updatedAt = uint64(block.timestamp);
        j.expiresAt = newExpiresAt;

        _openAdd(jobId);

        emit JobReopened(jobId, newExpiresAt);
    }

    /*//////////////////////////////////////////////////////////////
                        FREELANCER APPLICATIONS
    //////////////////////////////////////////////////////////////*/
    function applyToJob(uint256 jobId) 
        external 
        whenNotPaused 
        nonReentrant 
    {
        _applyToJob(jobId, "", 0, 0);
    }

    function applyToJob(
        uint256 jobId,
        string calldata proposalURI,
        uint256 bidAmount,
        uint64 deliveryDays
    ) external whenNotPaused nonReentrant {
        _applyToJob(jobId, proposalURI, bidAmount, deliveryDays);
    }

    function _applyToJob(
        uint256 jobId,
        string memory proposalURI,
        uint256 bidAmount,
        uint64 deliveryDays
    ) internal {
        Job storage j = jobs[jobId];

        if (j.client == address(0)) revert JobNotFound();
        if (j.status != JobStatus.Open) revert NotOpen();
        if (j.expiresAt != 0 && j.expiresAt <= block.timestamp) revert NotExpired();

        if (address(freelancerFactory) != address(0)) {
            address profile = freelancerFactory.freelancerProfile(msg.sender);
            if (profile == address(0)) revert NotFreelancer();
        }

        if (applicantIndex[jobId][msg.sender] != 0) revert AlreadyApplied();

        uint64 nowTs = uint64(block.timestamp);

        uint256 idx = applicantsOf[jobId].length;
        applicantsOf[jobId].push(
            Applicant({
                freelancer: msg.sender,
                appliedAt: nowTs,
                proposalURI: proposalURI,
                bidAmount: bidAmount,
                deliveryDays: deliveryDays
            })
        );
        applicantIndex[jobId][msg.sender] = idx + 1;
        jobsAppliedBy[msg.sender].push(jobId);

        emit JobApplied(jobId, msg.sender, nowTs);
    }

    function withdrawApplication(uint256 jobId) 
        external 
        whenNotPaused 
        nonReentrant 
    {
        uint256 idxPlusOne = applicantIndex[jobId][msg.sender];
        if (idxPlusOne == 0) revert ApplicationNotFound();

        uint256 idx = idxPlusOne - 1;

        Applicant storage a = applicantsOf[jobId][idx];
        a.freelancer = address(0);
        a.appliedAt = 0;
        a.proposalURI = "";
        a.bidAmount = 0;
        a.deliveryDays = 0;

        applicantIndex[jobId][msg.sender] = 0;

        emit JobApplicationWithdrawn(jobId, msg.sender);
    }

    /*//////////////////////////////////////////////////////////////
                            FACTORY HOOKS
    //////////////////////////////////////////////////////////////*/
    function markAsHired(
        uint256 jobId,
        address freelancer,
        address escrow
    ) external whenNotPaused onlyAllowedFactory nonReentrant {

        if (freelancer == address(0) || escrow == address(0)) revert ZeroAddress();

        Job storage j = jobs[jobId];
        if (j.client == address(0)) revert JobNotFound();
        if (j.status != JobStatus.Open) revert AlreadyHiredOrClosed();

        if (requireApplicationToHire) {
            if (applicantIndex[jobId][freelancer] == 0) revert ApplicationNotFound();
        }

        j.hiredFreelancer = freelancer;
        j.escrow = escrow;
        j.status = JobStatus.Hired;
        j.updatedAt = uint64(block.timestamp);

        _openRemove(jobId);
        _refundBondIfAny(j);

        emit JobHired(jobId, j.client, freelancer, escrow);
    }

    function markAsCompleted(uint256 jobId) 
        external 
        whenNotPaused 
        onlyAllowedFactory 
    {
        Job storage j = jobs[jobId];

        if (j.client == address(0)) revert JobNotFound();
        if (j.status != JobStatus.Hired) revert InvalidStatus();

        j.status = JobStatus.Completed;
        j.updatedAt = uint64(block.timestamp);

        emit JobCompleted(jobId, j.client, j.hiredFreelancer, j.escrow);
    }

    function closeExpired(uint256 jobId) external {
        Job storage j = jobs[jobId];

        if (j.client == address(0)) revert JobNotFound();
        if (j.status != JobStatus.Open) revert NotOpen();
        if (j.expiresAt == 0 || block.timestamp < j.expiresAt) revert NotExpired();

        j.status = JobStatus.Expired;
        j.updatedAt = uint64(block.timestamp);

        _openRemove(jobId);

        emit JobExpiredClosed(jobId, j.expiresAt);
    }

    /*//////////////////////////////////////////////////////////////
                                VIEWS
    //////////////////////////////////////////////////////////////*/
    function getApplicants(
        uint256 jobId,
        uint256 offset,
        uint256 limit
    ) external view returns (address[] memory freelancers, uint64[] memory appliedAt) {
        Applicant[] storage list = applicantsOf[jobId];
        uint256 len = list.length;

        if (offset >= len) {
            return (new address[](0), new uint64[](0));
        }

        uint256 end = offset + limit;
        if (end > len) end = len;
        uint256 n = end - offset;

        freelancers = new address[](n);
        appliedAt = new uint64[](n);

        for (uint256 i = 0; i < n; i++) {
            Applicant storage a = list[offset + i];
            freelancers[i] = a.freelancer;
            appliedAt[i] = a.appliedAt;
        }
    }

    function getApplicantCount(uint256 jobId) external view returns (uint256) {
        return applicantsOf[jobId].length;
    }

    function getApplicantDetails(
        uint256 jobId,
        address freelancer
    )
        external
        view
        returns (
            address _freelancer,
            uint64 appliedAt,
            string memory proposalURI,
            uint256 bidAmount,
            uint64 deliveryDays
        )
    {
        uint256 idxPlusOne = applicantIndex[jobId][freelancer];
        if (idxPlusOne == 0) revert ApplicationNotFound();

        Applicant storage a = applicantsOf[jobId][idxPlusOne - 1];

        return (
            a.freelancer,
            a.appliedAt,
            a.proposalURI,
            a.bidAmount,
            a.deliveryDays
        );
    }

    function getJobsAppliedBy(address freelancer) external view returns (uint256[] memory) {
        return jobsAppliedBy[freelancer];
    }

    function getJob(uint256 jobId)
        external
        view
        returns (
            address client,
            string memory title,
            string memory descriptionURI,
            uint256 budgetUSDC,
            JobStatus status,
            address hiredFreelancer,
            address escrow,
            uint64 createdAt,
            uint64 updatedAt,
            uint64 expiresAt,
            bytes32[] memory tags,
            uint256 postingBond
        )
    {
        Job storage j = jobs[jobId];

        if (j.client == address(0)) revert JobNotFound();

        return (
            j.client,
            j.title,
            j.descriptionURI,
            j.budgetUSDC,
            j.status,
            j.hiredFreelancer,
            j.escrow,
            j.createdAt,
            j.updatedAt,
            j.expiresAt,
            j.tags,
            j.postingBond
        );
    }

    function jobsByClient(address clientAddr) external view returns (uint256[] memory) {
        return jobsOf[clientAddr];
    }

    function jobsByClientPaginated(
        address clientAddr,
        uint256 offset,
        uint256 limit
    ) external view returns (uint256[] memory out) {
        uint256 len = jobsOf[clientAddr].length;

        if (offset >= len) return new uint256[](0);

        uint256 end = offset + limit;
        if (end > len) end = len;

        uint256 n = end - offset;
        out = new uint256[](n);

        for (uint256 i = 0; i < n; i++) {
            out[i] = jobsOf[clientAddr][offset + i];
        }
    }

    function openJobs(
        uint256 offset,
        uint256 limit
    ) external view returns (uint256[] memory out) {
        uint256 len = openJobIds.length;

        if (offset >= len) return new uint256[](0);

        uint256 end = offset + limit;
        if (end > len) end = len;

        uint256 n = end - offset;
        out = new uint256[](n);

        for (uint256 i = 0; i < n; i++) {
            out[i] = openJobIds[offset + i];
        }
    }

    function totalOpenJobs() external view returns (uint256) {
        return openJobIds.length;
    }

    function totalJobsOf(address clientAddr) external view returns (uint256) {
        return jobsOf[clientAddr].length;
    }

    /*//////////////////////////////////////////////////////////////
                        OWNER: TREASURY SWEEPS
    //////////////////////////////////////////////////////////////*/
    function sweepERC20(address /*token*/, uint256 amount) external onlyOwner {
        usdc.transfer(owner, amount);
    }

    /*//////////////////////////////////////////////////////////////
                        INTERNAL: OPEN SET
    //////////////////////////////////////////////////////////////*/
    function _openAdd(uint256 jobId) internal {
        if (openIndex[jobId] != 0) return;
        openJobIds.push(jobId);
        openIndex[jobId] = openJobIds.length;
    }

    function _openRemove(uint256 jobId) internal {
        uint256 idxPlusOne = openIndex[jobId];
        if (idxPlusOne == 0) return;

        uint256 idx = idxPlusOne - 1;
        uint256 lastId = openJobIds[openJobIds.length - 1];

        if (idx != openJobIds.length - 1) {
            openJobIds[idx] = lastId;
            openIndex[lastId] = idx + 1;
        }

        openJobIds.pop();
        openIndex[jobId] = 0;
    }

    function _refundBondIfAny(Job storage j) internal {
        uint256 bond = j.postingBond;
        if (bond == 0) return;

        j.postingBond = 0;
        usdc.transfer(j.client, bond);
    }
}

contracts/JobEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "./FreelancerProfile.sol";

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @title JobEscrow
/// @notice Single-use USDT escrow for a client↔freelancer job, integrated with FreelancerProfile for status & reputation.
contract JobEscrow is ReentrancyGuard {
    using SafeERC20 for IERC20;

    /*//////////////////////////////////////////////////////////////
                                ERRORS
    //////////////////////////////////////////////////////////////*/
    error ZeroAddress();
    error NotClient();
    error NotFreelancer();
    error NotResolver();
    error NotFactory();
    error AlreadyDelivered();
    error NotDelivered();
    error AlreadyTerminal();
    error CancelWindowOver();
    error CancelNotRequested();
    error InvalidCaller();
    error AmountZero();
    error NoDispute();
    error Disputed();
    error TooEarly();
    error BpsTooHigh();

    /*//////////////////////////////////////////////////////////////
                                EVENTS
    //////////////////////////////////////////////////////////////*/
    event WorkDelivered(bytes32 indexed jobKey, string uri);
    event CancelRequested(bytes32 indexed jobKey, address indexed by);
    event CancelAccepted(bytes32 indexed jobKey);
    event DisputeRaised(bytes32 indexed jobKey, address indexed by, string reasonURI);
    event DisputeResolved(bytes32 indexed jobKey, Resolution outcome, uint16 payoutBps, uint8 rating, uint256 paidToFreelancer, uint256 refundedToClient);
    event Paid(bytes32 indexed jobKey, address indexed to, uint256 netAmount, uint256 feeAmount);
    event Refunded(bytes32 indexed jobKey, address indexed to, uint256 amount);
    event AutoApproved(bytes32 indexed jobKey, uint8 rating);

    /*//////////////////////////////////////////////////////////////
                                IMMUTABLES
    //////////////////////////////////////////////////////////////*/
    address public immutable client;
    address public immutable freelancer;
    IERC20  public immutable usdt;
    uint256 public immutable amount;           // funded once at creation
    uint16  public immutable platformFeeBps;   // <= 10000
    address public immutable platformWallet;
    address public immutable resolver;
    FreelancerProfile public immutable profile;
    address public immutable escrowFactory;

    // Timelines / policy
    uint64 public immutable cancelWindowEnd;   // now + cancelWindowSecs
    uint64 public immutable deliveryDue;       // 0 disables hard delivery deadline
    uint64 public immutable reviewWindowSecs;  // 0 disables auto-approve
    uint8  public immutable autoApproveDefaultRating;

    /*//////////////////////////////////////////////////////////////
                                STATE
    //////////////////////////////////////////////////////////////*/
    bytes32 public jobKey;

    bool   public delivered;
    bool   public disputed;
    bool   public terminal;

    uint64 public reviewDue;              // set on delivery if reviewWindowSecs > 0
    address public cancelRequestedBy;     // tracks mutual cancel intent

    string  public lastDeliveryURI;
    string  public lastDisputeURI;

    /*//////////////////////////////////////////////////////////////
                                CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/
    constructor(
        address _client,
        address _freelancer,
        address _usdt,
        uint256 _amountUSDT,
        string memory /* _metadataURI */,
        uint16  _platformFeeBps,
        address _platformWallet,
        address _resolver,
        address _profile,
        uint64  _cancelWindowSecs,
        uint64  _deliveryDueTs,
        uint64  _reviewWindowSecs,
        uint8   _autoApproveDefaultRating,
        address _escrowFactory
    ) {
        if (_client == address(0) || _freelancer == address(0) || _usdt == address(0) || _platformWallet == address(0) || _resolver == address(0) || _profile == address(0) || _escrowFactory == address(0)) {
            revert ZeroAddress();
        }
        if (_amountUSDT == 0) revert AmountZero();
        if (_platformFeeBps > 10_000) revert BpsTooHigh();

        client = _client;
        freelancer = _freelancer;
        usdt = IERC20(_usdt);
        amount = _amountUSDT;

        platformFeeBps = _platformFeeBps;
        platformWallet = _platformWallet;
        resolver = _resolver;
        profile = FreelancerProfile(_profile);
        escrowFactory = _escrowFactory;

        cancelWindowEnd = uint64(block.timestamp) + _cancelWindowSecs;
        deliveryDue = _deliveryDueTs;
        reviewWindowSecs = _reviewWindowSecs;
        autoApproveDefaultRating = _autoApproveDefaultRating;

        // Unique job key bound to this escrow + parties
        jobKey = keccak256(abi.encode(address(this), _client, _freelancer));
    }

    /*//////////////////////////////////////////////////////////////
                                MODIFIERS
    //////////////////////////////////////////////////////////////*/
    modifier onlyClient() {
        if (msg.sender != client) revert NotClient();
        _;
    }

    modifier onlyFreelancer() {
        if (msg.sender != freelancer) revert NotFreelancer();
        _;
    }

    modifier onlyResolver() {
        if (msg.sender != resolver) revert NotResolver();
        _;
    }

    modifier onlyFactory() {
        if (msg.sender != escrowFactory) revert NotFactory();
        _;
    }

    modifier notTerminal() {
        if (terminal) revert AlreadyTerminal();
        _;
    }

    /*//////////////////////////////////////////////////////////////
                        BOOTSTRAP (factory after linking)
    //////////////////////////////////////////////////////////////*/
    /// @notice Factory calls this after linking the escrow in the profile to create a job and set it InProgress.
    function bootstrapRegisterJob() external onlyFactory {
        // Factory has already registered this escrow as active in the profile.
        profile.registerJob(jobKey); // sets status = Created
        profile.updateJobStatus(jobKey, FreelancerProfile.JobStatus.InProgress);
    }

    /*//////////////////////////////////////////////////////////////
                                CORE ACTIONS
    //////////////////////////////////////////////////////////////*/

    /// @notice Freelancer marks the work delivered; starts client review timer if configured.
    function deliverWork(string calldata deliveryURI) external onlyFreelancer notTerminal {
        if (delivered) revert AlreadyDelivered();

        delivered = true;
        lastDeliveryURI = deliveryURI;

        // Set review due if a window is configured
        if (reviewWindowSecs > 0) {
            reviewDue = uint64(block.timestamp) + reviewWindowSecs;
        }

        // Update profile status → Delivered
        profile.updateJobStatus(jobKey, FreelancerProfile.JobStatus.Delivered);

        emit WorkDelivered(jobKey, deliveryURI);
    }

    /// @notice Client approves the work; pays freelancer and finalizes reputation.
    function approveWork(uint8 rating) external onlyClient notTerminal nonReentrant {
        if (!delivered) revert NotDelivered();
        if (disputed) revert Disputed();

        _payoutAndComplete(rating);
    }

    /// @notice Either party can request cancellation. Pre-delivery client cancels unilaterally within window.
    function requestCancel() external notTerminal {
        if (msg.sender != client && msg.sender != freelancer) revert InvalidCaller();

        if (!delivered) {
            if (msg.sender == client && block.timestamp <= cancelWindowEnd) {
                // Unilateral pre-delivery cancel → refund client
                _refundAndCancel();
                return;
            }
        }
        cancelRequestedBy = msg.sender;
        emit CancelRequested(jobKey, msg.sender);
    }

    /// @notice Counterparty accepts a pending cancel request. Refunds client and cancels job.
    function acceptCancel() external notTerminal nonReentrant {
        if (cancelRequestedBy == address(0)) revert CancelNotRequested();
        if (msg.sender == cancelRequestedBy) revert InvalidCaller(); // must be the other party

        _refundAndCancel();
        emit CancelAccepted(jobKey);
    }

    /// @notice Either party raises a dispute, sending the profile to Disputed.
    function raiseDispute(string calldata reasonURI) external notTerminal {
        if (msg.sender != client && msg.sender != freelancer) revert InvalidCaller();
        if (disputed) revert Disputed();

        disputed = true;
        lastDisputeURI = reasonURI;

        profile.updateJobStatus(jobKey, FreelancerProfile.JobStatus.Disputed);
        emit DisputeRaised(jobKey, msg.sender, reasonURI);
    }

    /*//////////////////////////////////////////////////////////////
                                RESOLUTION / TIMEOUTS
    //////////////////////////////////////////////////////////////*/

    enum Resolution { PAYOUT, REFUND }

    /// @notice Resolver settles disputes. Supports full/partial payout with rating, or full refund.
    /// @param payoutBps 0..10000 share to freelancer from current escrow balance
    /// @param rating    applied only if payout > 0; clamped to 1..5
    /// @param outcome   PAYOUT (partial/full) or REFUND
    function resolveDispute(
        uint16 payoutBps,
        uint8  rating,
        Resolution outcome
    ) external onlyResolver notTerminal nonReentrant {
        if (!disputed) revert NoDispute();
        if (payoutBps > 10_000) revert BpsTooHigh();

        uint256 escrowBal = usdt.balanceOf(address(this));

        if (outcome == Resolution.PAYOUT) {
            uint256 toFreelancer = (escrowBal * payoutBps) / 10_000;
            uint256 toClient = escrowBal - toFreelancer;

            uint256 feeAmt = 0;
            uint256 netPayout = toFreelancer;

            if (toFreelancer > 0) {
                // clamp rating 1..5
                if (rating < 1) rating = 1;
                if (rating > 5) rating = 5;

                if (platformFeeBps > 0) {
                    feeAmt = (toFreelancer * platformFeeBps) / 10_000;
                    netPayout = toFreelancer - feeAmt;
                    if (feeAmt > 0) usdt.safeTransfer(platformWallet, feeAmt);
                }
                if (netPayout > 0) {
                    usdt.safeTransfer(freelancer, netPayout);
                    emit Paid(jobKey, freelancer, netPayout, feeAmt);
                }

                // Profile: treat as acceptance (even partial) → Approved + Completed with rating
                profile.updateJobStatus(jobKey, FreelancerProfile.JobStatus.Approved);
                profile.markJobCompleted(rating, jobKey);
            } else {
                // No payout → pure refund path (toClient == escrowBal)
                profile.updateJobStatus(jobKey, FreelancerProfile.JobStatus.Cancelled);
            }

            if (toClient > 0) {
                usdt.safeTransfer(client, toClient);
                emit Refunded(jobKey, client, toClient);
            }

            terminal = true;
            emit DisputeResolved(jobKey, outcome, payoutBps, rating, toFreelancer, toClient);
            return;
        }

        // outcome == REFUND
        _refundAndCancel();
        emit DisputeResolved(jobKey, outcome, 0, 0, 0, escrowBal);
    }

    /// @notice Processes timeouts:
    /// - If delivered & reviewDue passed & not disputed → auto-approve & pay with default rating.
    /// - If not delivered & deliveryDue set & passed → refund & cancel.
    function processTimeouts() external notTerminal nonReentrant {
        // Auto-approve if client silent after delivery
        if (delivered && !disputed && reviewWindowSecs > 0 && reviewDue != 0 && block.timestamp > reviewDue) {
            uint8 rating = autoApproveDefaultRating;
            if (rating < 1) rating = 1;
            if (rating > 5) rating = 5;
            _payoutAndComplete(rating);
            emit AutoApproved(jobKey, rating);
            return;
        }

        // Missed delivery deadline → client-friendly refund
        if (!delivered && deliveryDue != 0 && block.timestamp > deliveryDue) {
            _refundAndCancel();
            return;
        }

        revert TooEarly();
    }

    /*//////////////////////////////////////////////////////////////
                                INTERNALS
    //////////////////////////////////////////////////////////////*/

    function _payoutAndComplete(uint8 rating) internal {
        // clamp rating
        if (rating < 1) rating = 1;
        if (rating > 5) rating = 5;

        uint256 bal = usdt.balanceOf(address(this));
        uint256 feeAmt = 0;
        uint256 net = bal;

        if (platformFeeBps > 0) {
            feeAmt = (bal * platformFeeBps) / 10_000;
            if (feeAmt > 0) {
                net = bal - feeAmt;
                usdt.safeTransfer(platformWallet, feeAmt);
            }
        }
        if (net > 0) {
            usdt.safeTransfer(freelancer, net);
            emit Paid(jobKey, freelancer, net, feeAmt);
        }

        profile.updateJobStatus(jobKey, FreelancerProfile.JobStatus.Approved);
        profile.markJobCompleted(rating, jobKey);

        terminal = true;
    }

    function _refundAndCancel() internal {
        uint256 bal = usdt.balanceOf(address(this));
        if (bal > 0) {
            usdt.safeTransfer(client, bal);
            emit Refunded(jobKey, client, bal);
        }

        profile.updateJobStatus(jobKey, FreelancerProfile.JobStatus.Cancelled);
        terminal = true;
    }

    /*//////////////////////////////////////////////////////////////
                                VIEWS
    //////////////////////////////////////////////////////////////*/
    function currentDeadlines() external view returns (uint64 cancelEnd, uint64 _deliveryDue, uint64 _reviewDue) {
        return (cancelWindowEnd, deliveryDue, reviewDue);
    }
}

